import React,{useState} from 'react'
import Layout from '../../components/Layout/Layout'
import axios from 'axios'
import toast from 'react-hot-toast';
import { useNavigate } from 'react-router-dom'

const Register = () => {
    const [name,setName]=useState("");
    const [email,setEmail]=useState("");
    const [password,setPassword]=useState("");
    const [phone,setPhone]=useState("");
    const [address,setAddress]=useState("");

    const navigate=useNavigate()

    const handleSubmit=async(e)=>{
        e.preventDefault();
        try {
            const res=await axios.post(`${process.env.REACT_APP_API}/api/v1/auth/register`,{name,email,password,phone,address});
            if(res&&res.data.success){
                toast.success(res.data.message)
                navigate('/login')
            }
            else{
                toast.error(res.data.message)
            }
        } catch (error) {
            console.log(error)
            toast.error('Something went wrong')
        }
    };
    return (
    <Layout title={'Register Ecommerce -pp'}>
        <div className='register'>
            <h1>Register</h1>
  <form onSubmit={handleSubmit}>
  <div className="mb-3">

    <input type="text" value={name} onChange={(e)=>setName(e.target.value)} className="form-control" id="exampleInputName" placeholder="Enter your name" required  />
    
  </div>
  <div className="mb-3">
    
    <input type="email" value={email} onChange={(e)=>setEmail(e.target.value)} className="form-control" id="exampleInputName" placeholder="Enter your email" required  />
    
  </div>
  <div className="mb-3">
    
    <input type="password" value={password} onChange={(e)=>setPassword(e.target.value)} className="form-control" id="exampleInputPassword1" placeholder="Enter password" required  />

  </div>
  <div className="mb-3">
    
    <input type="number" value={phone} onChange={(e)=>setPhone(e.target.value)} className="form-control" id="exampleInputName" placeholder="Enter phone number" required />
    
  </div>
  <div className="mb-3">
    
    <input type="text" value={address} onChange={(e)=>setAddress(e.target.value)} className="form-control" id="exampleInputName" placeholder="Enter address"  required />
    
  </div>
  
  <button type="submit" className="btn btn-primary">Submit</button>
</form>

        </div>
    </Layout>
  )
}

export default Register
